def getAllPairs():

    pairs_ls = json.load(open('pooladdr.json', 'r'))
    pairs = []
    start = 41
    if len(pairs) > 0:
        start = pairs[-1]['index'] + 1
    for i in range(0, len(pairs_ls)):
        pair_addr = pairs_ls[i]
        
        try:            
            token0, token1,symbol0, symbol1, decimal0, decimal1, reserve0, reserve1 = token_addr(pair_addr)
            print("token0:", token0, symbol0, decimal0, reserve0)
            print("token1:", token1, symbol1, decimal1, reserve1)
            print('---------------------------------------------')

        except Exception as e:
            print(e)
            # problems.append(pair_addr)
            # json.dump(problems, open(problem_file, 'w'))
            continue
        if reserve0 == 0 or reserve1 == 0:
            	continue
        else:
	        pair = {'dex': 'spirit',
	                'index': start,
	                'address': pair_addr,
	                'token0': {
	                    'address': token0,
	                    'symbol': symbol0,
	                    'decimal': decimal0,
	                    },
	                'token1': {
	                    'address': token1,
	                    'symbol': symbol1,
	                    'decimal': decimal1,
	                    },
	                'reserve0': reserve0,
	                'reserve1': reserve1,
	                'r1': 997,
	                'r2':1000
	                }

	        # print(i, '/', 3, pair)
	        pairs.append(pair)
        start = start +1
    # print(pairs)
    print(len(pairs))
    with open("pairs_dexscreener_copy1.json", "w") as fp:
                json.dump(pairs, fp)
                print("File saved.")

getAllPairs()



